// ContentForge AI Lead Scoring Algorithm
const input = $input.first();
const lead = input.json;

// Initialize score
let score = 5;

// Email provided (+2 points)
if (lead.email && lead.email.includes('@')) {
  score += 2;
}

// Phone provided (+2 points)
if (lead.phone && lead.phone.length >= 10) {
  score += 2;
}

// Property address provided (+1 point)
if (lead.property_address && lead.property_address.length > 10) {
  score += 1;
}

// Aspen/Colorado area (+3 points)
if (lead.city && (lead.city.toLowerCase().includes('aspen') || 
    lead.state && lead.state.toLowerCase().includes('colorado'))) {
  score += 3;
}

// Lead source scoring
if (lead.lead_source) {
  if (lead.lead_source.includes('Zillow')) score += 2;
  if (lead.lead_source.includes('Referral')) score += 4;
}

// Time-based scoring
const now = new Date();
const captureTime = new Date(lead.date_captured);
const minutesDiff = (now - captureTime) / (1000 * 60);

if (minutesDiff < 60) score += 2;
else if (minutesDiff < 1440) score += 1;

// Cap score at 10
score = Math.min(score, 10);

// Generate insights
const insights = {
  score: score,
  priority: score >= 8 ? 'HIGH' : score >= 6 ? 'MEDIUM' : 'LOW',
  recommended_action: score >= 8 ? 'Create premium video + call immediately' : 
                     score >= 6 ? 'Create video + call within 4 hours' : 
                     'Create video + email follow-up',
  ai_notes: `ContentForge AI scored this lead ${score}/10. ` +
           `${score >= 8 ? 'HIGH-VALUE: Immediate attention required.' : 
             score >= 6 ? 'GOOD PROSPECT: Prompt follow-up needed.' : 
             'STANDARD: Regular follow-up protocol.'}`
};

return {
  json: {
    ...lead,
    priority_score: score,
    priority_level: insights.priority,
    recommended_action: insights.recommended_action,
    ai_insights: insights.ai_notes,
    contentforge_processed: 'true',
    processing_timestamp: now.toISOString()
  }
};

